// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract RegularPoints is ERC20 {
    // 映射存储用户指定的积分名称
    mapping(address => string) public customNames;

    constructor(string memory name, string memory symbol) ERC20(name, symbol) {}

    // 用户可以自定义名称
    function setCustomName(string memory name) external {
        customNames[msg.sender] = name;
    }

    // 用户可以自由铸造普通积分
    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    // 获取用户的自定义名称
    function getCustomName(address user) external view returns (string memory) {
        return customNames[user];
    }
}

contracts/UniversalPoints.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract UniversalPoints is ERC20("UniversalPoints", " UPT"), Ownable(address(msg.sender)) {
    // 初始化通用积分总发行量
    constructor(uint256 initialSupply){
        _mint(msg.sender, initialSupply * (10 ** uint256(decimals())));
    }

    // 只有合约拥有者可以发行通用积分（UPT）
    function issueUPT(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }
}

